import React from 'react';
import UserService from '../../services/user.service';
import BookService from '../../services/book.service';

export default class ProfilePage extends React.Component {

    constructor(props) {
        super(props);

        if (!UserService.currentUserValue) {
            this.props.history.push('/');
            return;
        }

        this.state = {
            user: UserService.currentUserValue,
            orders: []
        };
    }

    componentDidMount() {
        this.setState({
            orders: {loading: true}
        });
        const user = this.state.user;
        BookService.filterOrders(user.id).then(orders => {
            this.setState({orders: orders.data});
        });
    }

    render() {
        const {orders} = this.state;
        return (
            <div className="col-md-12">
                <div className="jumbotron">
                    <h1 className="display-4">Hello, {this.state.user.name}</h1>
                </div>
                {orders.loading && <em>Loading orders...</em>}
                {orders.length > 0 &&
                    <table className="table table-striped">
                        <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Book Name</th>
                            <th scope="col">Book Author</th>
                            <th scope="col">Description</th>
                            <th scope="col">Buy Date</th>
                        </tr>
                        </thead>
                        <tbody>
                        {orders.map((order, index) =>
                            <tr key={order.id}>
                                <th scope="row">{index + 1}</th>
                                <td>{order.book.name}</td>
                                <td>{order.book.author}</td>
                                <td>{order.book.description}</td>
                                <td>{order.dateOfIssue}</td>
                            </tr>
                        )
                        }
                        </tbody>
                    </table>
                }
                {orders.length === 0 &&
                    <div>No orders so far</div>
                }
            </div>
        );
    }

}
