import React from 'react';
import ProductService from '../../services/product.service';

export default class DefaultPage extends React.Component {

    constructor(props) {
        super(props);

        this.state = {
            id: this.props.match.params.id,
            product: JSON.parse(localStorage.getItem('currentProduct')),
            clients: [],
        };
    }

    componentDidMount() {
        this.findClientsOfCar();
    }

    findClientsOfCar() {
        ProductService.filterClients(this.state.id).then(clients => {
            this.setState({clients: clients.data});
        });
    }

    render() {
        const {clients} = this.state;
        return (
            <div className="col-md-12">
                <div className="jumbotron">
                    <h1 className="display-4">Product: {this.state.product.name}</h1>
                    <h3>Product description: {this.state.product.description}</h3>
                    <h3>Product price: {this.state.product.price}</h3>
                </div>
                <div><h5>Clients that bought this product so far</h5></div>
                {clients.length &&
                    <table className="table table-striped">
                        <thead>
                        <tr>
                            <th scope="col">#</th>
                            <th scope="col">Client Name</th>
                        </tr>
                        </thead>
                        <tbody>
                        {clients.map((client, index) =>
                            <tr key={client}>
                                <th scope="row">{index + 1}</th>
                                <td>{client}</td>
                            </tr>
                        )}
                        </tbody>
                    </table>
                }
                {clients.length === 0 &&
                    <div>No user has bought this item so far</div>
                }
            </div>
        );
    }

}
